mapper.insert:

insert into sys_token (id, access_token, expiration, 
  user_id, deleted, create_time, 
  create_user, update_time, update_user
  )
values (#{id,jdbcType=INTEGER}, #{accessToken,jdbcType=VARCHAR}, #{expiration,jdbcType=BIGINT},
  #{userId,jdbcType=INTEGER}, #{deleted,jdbcType=CHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}
  )

mapper.select:
select access_token
from sys_token

   and user_id = #{id}
   and access_token = #{accessToken}
